import React, { Component } from "react";
import axios from "axios";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

import LabelManager from "./LabelManager";

export default class EditTask extends Component {
  constructor(props) {
    super(props);

    this.onChangeUsername = this.onChangeUsername.bind(this);
    this.onChangeTitle = this.onChangeTitle.bind(this);
    this.onChangeDescription = this.onChangeDescription.bind(this);
    this.onChangeStatus = this.onChangeStatus.bind(this);
    this.onChangeLabels = this.onChangeLabels.bind(this);
    this.onChangeDueDate = this.onChangeDueDate.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      username: "",
      title: "",
      description: "",
      status: "",
      labels: [],
      dueDate: new Date(),
      users: []
    };
  }

  componentDidMount() {
    axios
      .get("/tasks/" + this.props.match.params.id)
      .then(response => {
        this.setState({
          username: response.data.username,
          title: response.data.title,
          description: response.data.description,
          status: response.data.status,
          labels: response.data.labels,
          dueDate: new Date(response.data.due_date)
        });
      })
      .catch(function(error) {
        console.log(error);
      });

    axios
      .get("/users/")
      .then(response => {
        if (response.data.length > 0) {
          this.setState({
            users: response.data.map(user => user.username)
          });
        }
      })
      .catch(error => {
        console.log(error);
      });

      axios
      .get("/labels/")
      .then(response => {
        if (response.data.length > 0) {
          this.setState({
            labels: response.data.map(label => label.label)
          });
        }
      })
      .catch(error => {
        console.log(error);
      });
  }

  onChangeUsername(e) {
    this.setState({
      username: e.target.value
    });
  }

  onChangeTitle(e) {
    this.setState({
      title: e.target.value
    });
  }

  onChangeDescription(e) {
    this.setState({
      description: e.target.value
    });
  }

  onChangeStatus(e) {
    this.setState({
      status: e.target.value
    });
  }

  onChangeLabels(e) {
    this.setState({
      labels: e.target.value
    });
  }

  onChangeDueDate(date) {
    this.setState({
      dueDate: date
    });
  }

  listLabelOptions() {
    const options = this.state.labels.map(currentLabel => {
      return (
        <option value={currentLabel} key={currentLabel._id}>
          {currentLabel}
        </option>
      );
    });
  }

  onSubmit(e) {
    e.preventDefault();

    const task = {
      username: this.state.username,
      title: this.state.title,
      description: this.state.description,
      status: this.state.status,
      labels: this.state.labels,
      due_date: this.state.dueDate
    };

    console.log(task);

    axios
      .post(
        "/tasks/update/" + this.props.match.params.id,
        task
      )
      .then(res => console.log(res.data));

    window.location = "/taskList";
  }

  render() {
    return (
      <div>
        <h3>Edit Task</h3>
        <form onSubmit={this.onSubmit}>
          <div className="form-group">
            <label>Username: </label>
            <select
              ref="userInput"
              required
              className="form-control"
              value={this.state.username}
              onChange={this.onChangeUsername}
            >
              {this.state.users.map(function(user) {
                return (
                  <option key={user} value={user}>
                    {user}
                  </option>
                );
              })}
            </select>
          </div>
          <div className="form-group">
            <label>Title: </label>
            <input
              type="text"
              required
              className="form-control"
              value={this.state.title}
              onChange={this.onChangeTitle}
            />
          </div>
          <div className="form-group">
            <label>Description: </label>
            <input
              type="text"
              required
              className="form-control"
              value={this.state.description}
              onChange={this.onChangeDescription}
            />
          </div>
          <div className="form-group">
            <label>Status: </label>
            <select
              ref="userInput"
              required
              className="form-control"
              value={this.state.status}
              onChange={this.onChangeStatus}
            >
              <option value="Not Started">Not Started</option>
              <option value="In Progress">In Progress</option>
              <option value="Complete">Complete</option>
            </select>
          </div>
          <div className="form-group">
            <label>Labels: </label>
            <select
              ref="userInput"
              optional
              className="form-control"
              value={this.state.labels}
              onChange={this.onChangeLabels}
            >
              {this.state.labels.map(label => {
                return (
                  <option key={label} value={label}>
                    {label}
                  </option>
                );
              })}
            </select>
          </div>
          <LabelManager />
          <br />
          <div className="form-group">
            <label>Due Date: </label>
            <div>
              <DatePicker
                selected={this.state.dueDate}
                onChange={this.onChangeDueDate}
              />
            </div>
          </div>

          <div className="form-group">
            <input
              type="submit"
              value="Edit Task"
              className="btn btn-primary"
            />
          </div>
        </form>
      </div>
    );
  }
}
